// Extraer texto crudo de Gemini
const rawText = $json.content.parts[0].text;

// LIMPIEZA simple
let cleanText = rawText
  .replace(/```json/g, '')  // elimina ```json
  .replace(/```/g, '')      // elimina ```
  .replace(/\\"/g, '"')     // corrige comillas escapadas
  .trim();

// Parsear JSON
let recommendations = [cleanText]; // fallback
try {
  recommendations = JSON.parse(cleanText);
} catch (e) {
  // Si falla, dividir por lÃ­neas
  recommendations = cleanText
    .split('\n')
    .map(l => l.trim())
    .filter(l => l.length > 0);
}

// Formato email en HTML
const formattedText = recommendations
  .map(item => `â€¢ ${item}`)
  .join('<br><br>');

const generated_text = `
